$sql_create_table[] = "INSERT INTO `" . $db_config['prefix'] . "_vi_about` (`id`, `title`, `alias`, `image`, `imagealt`, `imageposition`, `description`, `bodytext`, `keywords`, `socialbutton`, `activecomm`, `layout_func`, `weight`, `admin_id`, `add_time`, `edit_time`, `status`, `hitstotal`, `hot_post`) VALUES (9, 'Giới thiệu Quán dì Mười', 'quan-di-muoi', 'di-muoi.png', 'Dì Mười', 2, 'Chuyên cung cấp hàng tươi ngon chất lượng nhất.<br />Đi chợ giúp bạn không tính phí.<br />Bất cứ lúc nào, alo là có.', '<h2 style=\"font-style:italic;\"><span style=\"font-family:Arial,Helvetica,sans-serif;\">Thân chào các anh chị bà con cô bác!<br /> <br /> Tôi là Nguyễn Bá Lâm, tôi được sinh ra và lớn lên tại TPHCM, công việc của tôi liên quan nhiều đến máy tính, bản thân cũng là CEO của vài cty nhỏ như in ấn, truyền thông, kinh doanh tuy nhiên tôi lại có 1 niềm đam mê ẩm thực cháy bỏng, có lẽ mọi thứ đều được thừa hưởng từ người mẹ của tôi, người được bà con thân thiết gọi với một cái tên không thể dân dã hơn &quot; dì mười &quot;. Nay tôi xin được đẩy lên thành một mỹ từ, đó cũng là &quot; Dì Mười &quot; .. ^ ^<br /> <br /> Dì Mười từ nhỏ có một tuổi thơ cơ cực, sống trong một gia đình có tới 11 người con, 8 tuổi đã phải nấu ăn cho cả nhà, vóc người nhỏ bé, bếp thì cao, gác khúc cây nhón chân nấu bị đổ canh phỏng nhiều lần, hơn 10 tuổi đã phải buôn gánh bán bưng kiếm sống. Một tuổi thơ không mấy êm đềm đã đúc kết nên một Dì Mười ngày nay cái gì cũng biết làm, món gì cũng biết nấu, đặc biệt là các món ăn dân dã miền quê, đậm chất Việt.<br /> <br /> Với số vốn kinh nghiệm quí báu như vậy, nếu không dùng để truyền cho thê hệ sau thì còn làm gì nữa hả các bạn, tôi quyết định làm cầu nối để mang những kiến thức sâu sắc về ẩm thực Việt cũng như những ký năng liên quan của Dì Mười đến cho các anh chị bà con cô bác.<br /> <br /> Trái cây lựa từng trái, trứng lựa từng quả, thịt lựa&nbsp;từng miếng, gà, cá, tôm lựa từng con .v.v...tất cả mọi thứ các anh chị chỉ cần alo là có và đặc biệt là hàng ngon nhất, chất lượng nhất, không có trong danh mục cũng có thể &nbsp;đặt hàng thêm trong ghi chú, Quán Dì Mười sẽ đi mua giúp ngoài danh mục mà không tính thêm bất cứ loại phí gì.</span><br /> &nbsp;</h2>', 'giới thiệu', 1, '4', '', 1, 1, 1634357916, 1634441052, 1, 3, 1)";

